CREATE VIEW prdatraval AS        SELECT c1.attribute_id, c1.attrtype_id, c1.attrvalue_id, c1.catentry_id, c1.field1, c1.field2, c1.field3, c1.floatvalue, c1.image1, c1.image2, c1.integervalue, c1.language_id, c1.name, c1.oid, c1.sequence, c1.stringvalue, c2.partnumber        FROM attrvalue c1, catentry c2        WHERE c1.catentry_id = c2.catentry_id;

