CREATE VIEW pratrstatr (attr_product_id, attr_id, lang_id, attr_name, type_id, attr_partnumber)  AS        SELECT c1.catentry_id, c1.attribute_id, c1.language_id, c1.name, c1.attrtype_id, c2.partnumber        FROM attribute c1, catentry c2        WHERE c1.catentry_id = c2.catentry_id;

