CREATE VIEW prodatr AS        SELECT c1.attr_name, c2.name, c1.attr_partnumber, c2.partnumber, c1.attr_product_id, c2.catentry_id, c2.attribute_id, c2.attrtype_id, c2.attrvalue_id, c2.field1, c2.field2, c2.field3, c2.floatvalue, c2.image1, c2.image2, c2.integervalue, c2.language_id, c2.oid, c2.sequence, c2.stringvalue        FROM pratrstatr c1, prdatraval c2        WHERE c1.attr_id = c2.attribute_id                 AND    c1.type_id = c2.attrtype_id;

